            <script type="text/javascript">
                var prev_screen = "signin";
                $(document).ready(function(){
                    $("#btn-exit").click(function(){
                        if(prev_screen === "signin")
                        {
                            history.back();
                        }
                        if(prev_screen === "signup")
                        {
                            history.back();
                        }
                    });
                    $("input:radio[name='license_type']").each(function(){
                        var type = $(this).val();
                        $(this).click(function(){
                               if (type === "term")
                               {
                                   $("#policy").hide();
                                   $("#term").show();
                               }else{
                                   $("#term").hide();
                                   $("#policy").show();
                               }
                        });
                    });
                    
                });
                function openLicenseText(type){
                    if (type === "term")
                               {
                                   $(".policy").hide();
                                   $(".term").show();
                               }else{
                                   $(".term").hide();
                                   $(".policy").show();
                               }
                }
            </script>
    

